import { createRouter, createWebHistory } from 'vue-router'
import Home from '../views/Home.vue'
import About from '../views/About.vue'
import NotFound from '../views/NotFound.vue'
import{
  getAuth,
  createUserWithEmailAndPassword
} from 'firebase/auth'
import {
  getFirestore,
  doc
} from 'firebase/firestore'
import {initializeApp} from 'firebase/app'

const firebaseConfig = {
  apiKey: "AIzaSyChqIyvkSsAw3yT6DKzJMYPhu5FYE3TMR4",
  authDomain: "firstproj-a4a91.firebaseapp.com",
  projectId: "firstproj-a4a91",
  storageBucket: "firstproj-a4a91.appspot.com",
  messagingSenderId: "727671980380",
  appId: "1:727671980380:web:936fbf174f702678d84199"
};

initializeApp(firebaseConfig)

const db = getFirestore()

const auth = getAuth()

//signing users up
const signupForm = document.querySelector('.signup')
signupForm.addEventListener('submit', (e)=>{
  e.preventDefault()
  const email = signupForm.email.value
  const password = signupForm.password.value

  createUserWithEmailAndPassword(auth, email, password)
    .then((cred)=>{
      console.log('user created:',cred.user)
      signupForm.reset()
    })
    .catch((err)=>{
      console.log(err.message)
    })
})





const routes = [
  {
    path: '/',
    name: 'Home',
    component: Home
  },
  {
    path: '/about',
    name: 'About',
    component: About
  },
  {
    path:'/jobs',
    redirect:'/'
  },
  {
    path:'/:catchAll(.*)',
    name: 'NotFound',
    component: NotFound
  }

]

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes
})

export default router
